"use strict";function getJsonFile(){fetch("http://localhost:3000/api/teddies/").then((response)=>{if(response.ok){response.json().then((data)=>{showTeddies(data);})}else{alert('Une erreur est survenue : Requête non acceptée \n'+'Status code de la réponse : '+response.status+': '+response.statusText);}}).catch((error)=>console.log("error : "+error));}
function showTeddies(teddies){teddies.forEach((teddy)=>{let mainContent=document.querySelector(".content_js");let myArticle=document.createElement("article");let myImg=document.createElement("img");let myH2=document.createElement("h2");let myH3=document.createElement("h3");let myA=document.createElement("a");myArticle.classList.add("cards");myImg.setAttribute("src",teddy.imageUrl);myImg.setAttribute("alt","Photo de l'ours en peluche");myImg.style.height="50%";myA.setAttribute("href","./product.html?id="+teddy._id);myA.classList.add("btn_product");myH2.textContent=teddy.name;myH3.textContent=teddy.price / 100+" €";myA.textContent="Voir le produit";myArticle.appendChild(myImg);myArticle.appendChild(myH2);myArticle.appendChild(myH3);myArticle.appendChild(myA);mainContent.appendChild(myArticle);});}
getJsonFile();